			public WebResourceResponse shouldInterceptRequest(WebView view, WebResourceRequest request) {
				if (Build.VERSION.SDK_INT >= 21) {
					String url = request.getUrl().toString();
					String key[] = {
							"leaderboard_bg.jpg", "lb_title.png", "leaderboard_avatar0.png", "leaderboard_avatar1.png",
							"leaderboard_avatar2.png", "leaderboard_avatar3.png", "leaderboard_avatar4.png", "leaderboard_avatar5.png",
							"leaderboard_back.png", "leaderboard_edit.png", "leaderboard_save.png", "leaderboard_score.png",
							"leaderboard_title.png", "leaberboard_style.css"
					};
					int i=0;
					for(i=0; i<key.length; i++) {
						if (url.contains(key[i])) {
							try {
								InputStream input = context.getAssets().open("images_leaderboard/" + key[i]);
								return new WebResourceResponse("image/png", "UTF-8", input);
							} catch (FileNotFoundException e) {
								e.printStackTrace();
							} catch (IOException e) {
								e.printStackTrace();
							}
							break;
						}
					}
					if (mCustomWebViewClient != null) {
						return mCustomWebViewClient.shouldInterceptRequest(view, request);
					}
					else {
						return super.shouldInterceptRequest(view, request);
					}
				}
				else {
					return null;
				}
			}

